import * as types from './../constants/ActionType';


export const addToCart = (product,quantity) =>{
    return{
        type: types.ADD_TO_CART,
        product,
        quantity
    }
}

export const accChangeMessage = (message) =>{
    return{
        type: types.CHANGE_MESSAGE,
        message
    }
}

export const accRemoveCart = (product) =>{
    return{
        type: types.CHANGE_MESSAGE,
        product
    }
}
